class NewsModel {
  NewsModel({
      String? title, 
      String? link, 
      List<String>? keywords, 
      List<String>? creator, 
      dynamic videoUrl, 
      String? description, 
      String? content, 
      String? pubDate, 
      String? fullDescription, 
      dynamic imageUrl, 
      String? sourceId, 
      List<String>? country, 
      List<String>? category, 
      String? language,}){
    _title = title;
    _link = link;
    _keywords = keywords;
    _creator = creator;
    _videoUrl = videoUrl;
    _description = description;
    _content = content;
    _pubDate = pubDate;
    _fullDescription = fullDescription;
    _imageUrl = imageUrl;
    _sourceId = sourceId;
    _country = country;
    _category = category;
    _language = language;
}

  NewsModel.fromJson(dynamic json) {
    _title = json['title'];
    _link = json['link'];
    _keywords = json['-"keywords"'] != null ? json['-"keywords"'].cast<String>() : [];
    _creator = json['-"creator"'] != null ? json['-"creator"'].cast<String>() : [];
    _videoUrl = json['video_url'];
    _description = json['description'];
    _content = json['content'];
    _pubDate = json['pubDate'];
    _fullDescription = json['full_description'];
    _imageUrl = json['image_url'];
    _sourceId = json['source_id'];
    _country = json['-"country"'] != null ? json['-"country"'].cast<String>() : [];
    _category = json['-"category"'] != null ? json['-"category"'].cast<String>() : [];
    _language = json['language'];
  }
  String? _title;
  String? _link;
  List<String>? _keywords;
  List<String>? _creator;
  dynamic _videoUrl;
  String? _description;
  String? _content;
  String? _pubDate;
  String? _fullDescription;
  dynamic _imageUrl;
  String? _sourceId;
  List<String>? _country;
  List<String>? _category;
  String? _language;
NewsModel copyWith({  String? title,
  String? link,
  List<String>? keywords,
  List<String>? creator,
  dynamic videoUrl,
  String? description,
  String? content,
  String? pubDate,
  String? fullDescription,
  dynamic imageUrl,
  String? sourceId,
  List<String>? country,
  List<String>? category,
  String? language,
}) => NewsModel(  title: title ?? _title,
  link: link ?? _link,
  keywords: keywords ?? _keywords,
  creator: creator ?? _creator,
  videoUrl: videoUrl ?? _videoUrl,
  description: description ?? _description,
  content: content ?? _content,
  pubDate: pubDate ?? _pubDate,
  fullDescription: fullDescription ?? _fullDescription,
  imageUrl: imageUrl ?? _imageUrl,
  sourceId: sourceId ?? _sourceId,
  country: country ?? _country,
  category: category ?? _category,
  language: language ?? _language,
);
  String? get title => _title;
  String? get link => _link;
  List<String>? get keywords => _keywords;
  List<String>? get creator => _creator;
  dynamic get videoUrl => _videoUrl;
  String? get description => _description;
  String? get content => _content;
  String? get pubDate => _pubDate;
  String? get fullDescription => _fullDescription;
  dynamic get imageUrl => _imageUrl;
  String? get sourceId => _sourceId;
  List<String>? get country => _country;
  List<String>? get category => _category;
  String? get language => _language;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['title'] = _title;
    map['link'] = _link;
    map['-"keywords"'] = _keywords;
    map['-"creator"'] = _creator;
    map['video_url'] = _videoUrl;
    map['description'] = _description;
    map['content'] = _content;
    map['pubDate'] = _pubDate;
    map['full_description'] = _fullDescription;
    map['image_url'] = _imageUrl;
    map['source_id'] = _sourceId;
    map['-"country"'] = _country;
    map['-"category"'] = _category;
    map['language'] = _language;
    return map;
  }

}